import React, { useContext } from "react";
import ArticleService from "../services/ArticleService";

export default class Orders extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      search: "",
      minRate:'',
      maxRate:''
    };

    this.changeSearch = this.changeSearch.bind(this);
    this.changeSearchRateMin = this.changeSearchRateMin.bind(this);
    this.changeSearchRateMax = this.changeSearchRateMax.bind(this);
    this.searchByRate = this.searchByRate.bind(this);
  }

  componentDidMount() {
    ArticleService.getOrders().then((response) => {
      this.setState({ articles: response.data });
    });
  }

  changeSearch = (event, value) => {
    ArticleService.getArticlesName(event.target.value).then((response) => {
      this.setState({ articles: response.data });
    });
  };

  changeSearchRateMin= (event, value) => {
      this.setState({minPrice:event.target.value})
  };

  changeSearchRateMax= (event, value) => {
    this.setState({maxPrice:event.target.value})
  };

  searchByRate = () =>{
    ArticleService.getArticlesPrice(this.state.minRate, this.state.maxRate).then((response) => {
        this.setState({ articles: response.data });
      });
  }



  render() {
    return (
      <div>
        <h1 className="text-center"> Artikli </h1>

        <div className="row">
          <label htmlFor="search">
            <input
              placeholder="Search"
              id="search"
              type="text"
              onChange={this.changeSearch}
            />
          </label>

          <label style={{margin:10}} htmlFor="minPrice">
            Rate from:
            <input
              placeholder="min"
              id="minPrice"
              type="number"
              onChange={this.changeSearchPriceMin}
            />
          </label>
          <label htmlFor="maxPrice">
            to:
            <input
              placeholder="max"
              id="maxPrice"
              type="number"
              onChange={this.changeSearchPriceMax}
            />
          </label>
          <button style={{height:32, margin:8}} onClick={this.searchByPrice}>Search</button>
        </div>
        <table className="table table-striped">
          <thead>
            <tr>
              <td>
                <b>Date</b>
              </td>
              <td>
                <b>Comment</b>
              </td>
              <td>
                <b>Rate</b>
              </td>
              <td>
                <b>Is Annonymous</b>
              </td>

            </tr>
          </thead>

          <tbody>
            {this.state.articles.map((article) => (
              <tr key={article.date}>
                {/* <td> <img alt="" src={`http://localhost:8096/api/articles/getImage/${article.image_src}`} width="70px" height="70px"/></td> */}
                <td> {article.date}</td>
                <td>{article.comment}</td>
                <td>{article.rate}</td>
                <td>{article.annonymousComment ? 'annonymous' : 'public'}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="btn btn-success" onClick={this.addArticle}>
          Add
        </button>
      </div>
    );
  }
}
