import React, { Component } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";

import AuthService from "../services/auth.service";
import ArticleService from "../services/ArticleService";

export default class ShoppingCart extends Component{
  constructor(props) {
    super(props);

    this.state = {
      articles: [],
      qty: "",
      price: "",
      totalPrice: ""
    };
  }

  componentDidMount() {
    ArticleService.getCarts().then(
      response => {
        this.setState({
          articles: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response && error.response.data) ||
            error.message ||
            error.toString() ||
            console.log("GRESKAAA")
        });
      }
    );
  }

  render() {
    return (
      <div className="col-md-12">
        <div className="container">
          <h1>Shopping Cart</h1>
          <div className="tableDiv">
            <table>
              <thead>
                <tr>
                  <td>Image</td>
                  <td>Name</td>
                  <td>Price</td>
                </tr>
              </thead>

              <tbody>
                {this.state.articles.map(art => 
                  <tr key = {art.product_id.id} className="tablerow">
                    <td> <img alt="" src={`http://localhost:8096/api/articles/getImage/${art.product_id.image_src}`} width="70px" height="70px"/></td>
                    <td>{art.product_id.name}</td>
                    <td>{art.product_id.price}</td>

                  </tr>
                )}
              </tbody>

            </table>
          </div>
        </div>

      </div>
    )
  }
}
